const os = require('os')

function sys(){
var memory = os.totalmem() / 1000000000
var all_memory = Math.round(memory)
var freememory = os.freemem() / 1000000000
var all_freememory = Math.round(freememory)
//==================================================
var ram = all_memory
var freeram = all_freememory
var name = os.hostname()
var osystem = os.type()
var node = process.version
var cpu = os.cpus().map(i => `${i.model}`)[0]
var arch = os.arch()
var res = JSON.parse(`{"name": "${name}", "osystem":"${osystem}","ram":"${ram}","freeram": "${freeram}","cpu": "${cpu}","arch":"${arch}","node":"${node}"}`)
return res
}
module.exports = sys;
